import React, { useState } from 'react';
import Products from './Products';


const AddProducts = () => {
    const [product, setProductData] = useState([]);
    var text,des;
    const nameHandler =(e)=>{
         text = e.target.value;
     
    }
    const desHandler=(e)=>{
         des = e.target.value;
    }
   const submitHandler=(e)=>{
       e.preventDefault();
       setProductData( [
            ...product,
           { name: text , description: des , id: Math.random()*1000 }

       ]);
   }

  return(
      <div className="container">
        <div className="row">
        <h1 className="page-title">Add Product</h1>       
        </div>
        {/* Form to add products  */}
        <div className="row text-start justify-content-center">
        <div className="col-6">
        <form>
        <div class="mb-3">
            <label class="form-label">Name</label>
            <input type="text" class="form-control" onChange={nameHandler}></input>          
        </div>
        <div class="mb-3">
            <label class="form-label">Description</label>
            <input type="text" class="form-control" onChange={desHandler}></input>
        </div>        
        <button type="submit" class="btn btn-primary" onClick={submitHandler}>Submit</button>
        </form>
            </div>
        </div>
        {/* <Products product={product} />   */}
        </div>
  )
}
 export default AddProducts;
