import React, { createContext, useState } from "react";

export const storeContext = createContext();
export const UserProvider = ({ children }) => {
   const [name , setName] = useState('jhon');

   return(
       <UserContext.Provider
       value={{
               name,setName
           }}
       >
           {children}
       </UserContext.Provider>
   );
};
